package com.bootreceiver.app.utilsimport android.content.Contextimport android.provider.Settingsimport android.util.Logobject DeviceIdManager {    fun getDeviceId(context: Context): String {        val androidId = Settings.Secure.getString(context.contentResolver, Settings.Secure.ANDROID_ID)        Log.d(TAG, "Device ID obtido: $androidId")        return androidId ?: "unknown_device"    }    private const val TAG = "DeviceIdManager"}