package com.bootreceiver.app.utilsimport android.app.Activityimport android.util.Logimport android.view.KeyEventimport android.view.WindowManager/** * Interceptador de eventos para prevenir minimiza├º├úo do app em modo kiosk *  * Intercepta eventos de teclas (Home, Back) e movimentos para minimizar * antes que aconte├ºam, mantendo o app sempre em foreground */class KioskModeInterceptor(private val activity: Activity) {        /**     * Intercepta o evento de pressionar o bot├úo Home     * Retorna true para consumir o evento e prevenir minimiza├º├úo     */    fun onKeyDown(keyCode: Int, event: KeyEvent?): Boolean {        if (keyCode == KeyEvent.KEYCODE_HOME) {            Log.d(TAG, "­ƒöÆ Bot├úo Home pressionado - bloqueado em modo kiosk")            return true // Consome o evento, previne minimiza├º├úo        }        return false    }        /**     * Intercepta o evento de pressionar o bot├úo Back     * Em modo kiosk, pode bloquear ou permitir (dependendo da configura├º├úo)     */    fun onBackPressed(): Boolean {        Log.d(TAG, "­ƒöÆ Bot├úo Back pressionado - bloqueado em modo kiosk")        return true // Consome o evento, previne voltar    }        /**     * Intercepta quando o usu├írio tenta sair da activity     * Chamado antes de onPause() quando o usu├írio pressiona Home ou muda de app     */    fun onUserLeaveHint() {        Log.d(TAG, "­ƒöÆ Tentativa de sair da activity detectada - bloqueando em modo kiosk")        // Move a task de volta para frente imediatamente        activity.moveTaskToBack(false)    }        /**     * Aplica configura├º├Áes de janela para prevenir minimiza├º├úo     */    fun applyWindowFlags() {        try {            val window = activity.window            val params = window.attributes                        // Previne que a janela seja movida para background            params.type = WindowManager.LayoutParams.TYPE_APPLICATION_OVERLAY            window.attributes = params                        // Adiciona flags adicionais            window.addFlags(WindowManager.LayoutParams.FLAG_DISMISS_KEYGUARD)            window.addFlags(WindowManager.LayoutParams.FLAG_SHOW_WHEN_LOCKED)            window.addFlags(WindowManager.LayoutParams.FLAG_TURN_SCREEN_ON)            window.addFlags(WindowManager.LayoutParams.FLAG_KEEP_SCREEN_ON)                        Log.d(TAG, "Ô£à Flags de janela aplicadas para prevenir minimiza├º├úo")        } catch (e: Exception) {            Log.e(TAG, "Erro ao aplicar flags de janela: ${e.message}", e)        }    }        companion object {        private const val TAG = "KioskModeInterceptor"    }}