package com.bootreceiver.app.utilsimport android.app.AppOpsManagerimport android.content.Contextimport android.content.Intentimport android.net.Uriimport android.os.Buildimport android.os.PowerManagerimport android.provider.Settingsimport android.util.Log/** * Classe utilit├íria para verificar permiss├Áes e otimiza├º├Áes de bateria *  * Verifica se o app tem todas as permiss├Áes necess├írias e se n├úo est├í * sendo otimizado pelo sistema (o que pode impedir o funcionamento no boot) */class PermissionChecker(private val context: Context) {        /**     * Verifica se todas as permiss├Áes necess├írias est├úo concedidas     *      * Nota: RECEIVE_BOOT_COMPLETED, INTERNET e ACCESS_NETWORK_STATE s├úo     * permiss├Áes normais que s├úo concedidas automaticamente no install.     * Apenas verificamos permiss├Áes especiais que requerem a├º├úo do usu├írio.     */    fun checkAllPermissions(): PermissionStatus {        val missingPermissions = mutableListOf<String>()                // Verificar permiss├úo de sobrepor outras apps (Android 6.0+)        // Esta ├® opcional mas recomendada para funcionar melhor        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.M) {            if (!Settings.canDrawOverlays(context)) {                missingPermissions.add("SYSTEM_ALERT_WINDOW (Sobrepor outras apps - opcional)")            }        }                // As outras permiss├Áes (RECEIVE_BOOT_COMPLETED, INTERNET, ACCESS_NETWORK_STATE)        // s├úo concedidas automaticamente e n├úo precisam verifica├º├úo                return if (missingPermissions.isEmpty()) {            PermissionStatus(true, emptyList())        } else {            PermissionStatus(false, missingPermissions)        }    }        /**     * Verifica se o app est├í sendo otimizado pela bateria     * Apps otimizados podem n├úo receber BOOT_COMPLETED     */    fun isBatteryOptimized(): Boolean {        return if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.M) {            try {                val powerManager = context.getSystemService(Context.POWER_SERVICE) as? PowerManager                powerManager?.let {                    !it.isIgnoringBatteryOptimizations(context.packageName)                } ?: false            } catch (e: Exception) {                Log.w(TAG, "Erro ao verificar otimiza├º├úo de bateria: ${e.message}")                false            }        } else {            false // Android < 6.0 n├úo tem otimiza├º├úo de bateria        }    }        /**     * Verifica se o app est├í sendo otimizado pelo sistema     * (diferente de otimiza├º├úo de bateria - espec├¡fico de alguns fabricantes)     *      * Nota: Esta verifica├º├úo pode n├úo funcionar em todas as vers├Áes do Android     * devido a limita├º├Áes de API. A verifica├º├úo de otimiza├º├úo de bateria ├® mais importante.     */    fun isSystemOptimized(): Boolean {        // Por enquanto, retorna false para evitar problemas de compatibilidade        // A verifica├º├úo de otimiza├º├úo de bateria ├® suficiente para a maioria dos casos        return false    }        /**     * Verifica status completo de permiss├Áes e otimiza├º├Áes     */    fun getFullStatus(): FullStatus {        val permissionStatus = checkAllPermissions()        val batteryOptimized = isBatteryOptimized()        val systemOptimized = isSystemOptimized()                val issues = mutableListOf<String>()        if (!permissionStatus.allGranted) {            issues.add("Permiss├Áes faltando: ${permissionStatus.missingPermissions.joinToString()}")        }        if (batteryOptimized) {            issues.add("App est├í sendo otimizado pela bateria")        }        if (systemOptimized) {            issues.add("App est├í sendo otimizado pelo sistema")        }                return FullStatus(            allPermissionsGranted = permissionStatus.allGranted,            batteryOptimized = batteryOptimized,            systemOptimized = systemOptimized,            issues = issues,            isReady = issues.isEmpty()        )    }        /**     * Abre as configura├º├Áes para desabilitar otimiza├º├úo de bateria     */    fun openBatteryOptimizationSettings() {        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.M) {            try {                val intent = Intent(Settings.ACTION_REQUEST_IGNORE_BATTERY_OPTIMIZATIONS).apply {                    data = Uri.parse("package:${context.packageName}")                    flags = Intent.FLAG_ACTIVITY_NEW_TASK                }                context.startActivity(intent)            } catch (e: Exception) {                Log.e(TAG, "Erro ao abrir configura├º├Áes de otimiza├º├úo: ${e.message}", e)                // Fallback: abrir configura├º├Áes gerais de bateria                try {                    val intent = Intent(Settings.ACTION_IGNORE_BATTERY_OPTIMIZATION_SETTINGS).apply {                        flags = Intent.FLAG_ACTIVITY_NEW_TASK                    }                    context.startActivity(intent)                } catch (e2: Exception) {                    Log.e(TAG, "Erro ao abrir configura├º├Áes gerais: ${e2.message}", e2)                }            }        }    }        /**     * Abre as configura├º├Áes para permitir sobrepor outras apps     */    fun openOverlayPermissionSettings() {        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.M) {            try {                val intent = Intent(Settings.ACTION_MANAGE_OVERLAY_PERMISSION).apply {                    data = Uri.parse("package:${context.packageName}")                    flags = Intent.FLAG_ACTIVITY_NEW_TASK                }                context.startActivity(intent)            } catch (e: Exception) {                Log.e(TAG, "Erro ao abrir configura├º├Áes de overlay: ${e.message}", e)            }        }    }        data class PermissionStatus(        val allGranted: Boolean,        val missingPermissions: List<String>    )        data class FullStatus(        val allPermissionsGranted: Boolean,        val batteryOptimized: Boolean,        val systemOptimized: Boolean,        val issues: List<String>,        val isReady: Boolean    )        companion object {        private const val TAG = "PermissionChecker"    }}